(()=>{"use strict";const t=document.querySelectorAll(".question");new class{constructor(t){this.questions=t,this.countingAll=0,this.paging=0,this.del=document.querySelector(".del"),this.submit=document.querySelector(".submit"),this.questions=t,this.init(),this.storedList={},this.addDelListener(),this.calculObj()}init(){this.questions.forEach(((t,s)=>{this.handleInput(t,s)}))}handleInput(t,s){t.querySelectorAll("input").forEach(((t,s)=>{t.addEventListener("click",(()=>{this.storeObj(String(t.getAttribute("name")),Number(t.getAttribute("value"))),this.paging+1>=this.questions.length?alert("마지막 문제입니다 제출하기를 눌러주세요!"):(this.questions[this.paging].classList.remove("on"),this.paging+=1,this.questions[this.paging].classList.add("on"),console.log(this.storedList))}))}))}addDelListener(){this.del&&this.del.addEventListener("click",(t=>{t.preventDefault(),0===this.paging?alert("뒤로 갈 수 없습니다"):(this.questions[this.paging].classList.remove("on"),this.paging-=1,this.paging>=0&&this.questions[this.paging].classList.add("on"))}))}storeObj(t,s){this.storedList[t]=s}calculObj(){var t;null===(t=this.submit)||void 0===t||t.addEventListener("click",(()=>{this.paging+1>=this.questions.length?(this.countingAll=Object.values(this.storedList).reduce(((t,s)=>t+s),0),window.location.href=`/public/result.html?counting=${this.countingAll}`):alert("문제에 모두 답변 후 제출해주세요!")}))}}(t)})();